// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import { Suave } from "lib/suave-std/src/suavelib/Suave.sol"; // todo fix remapping
import { EthJsonRPC } from "lib/suave-std/src/protocols/EthJsonRPC.sol"; // todo fix remapping
import { SuaveContract } from "./lib/SuaveContract.sol"; // todo fix remapping
import "./lib/AuctionUtils.sol";
import "./lib/SigUtils.sol";

// todo: add cancel bid
// todo instead of timelock on the vault release the funds with a signature (MEVM checks funds are not used)
// todo: accounting systems that prevents users to use their funds multiple times accross different auctions

contract TokenAuction is SuaveContract {

    event AuctionCreated(
        uint256 indexed auctionId,
        address indexed auctioneer,
        bytes32 indexed hashedToken,
        address payoutAddress,
        uint64 until,
        uint64 payoutCollectionDuration
    );
    event AuctionCancelled(uint indexed auctionId);
    event AuctionSettled(
        uint indexed auctionId,
        BidId winningBidId,
        AuctionPayout payout, 
        bytes payoutSig
    );
    event BidPlaced(
        uint indexed auctionId,
        BidId indexed bidId,
        address indexed bidder
    );
    // event BidCancelled(BidId indexed bidId);

    string constant PK_NAMESPACE = "auction:v0:pksecret";
    string constant BID_NAMESPACE = "auction:v0:bids";
    address[] public genericPeekers = [0xC8df3686b4Afb2BB53e60EAe97EF043FE03Fb829]; // todo: update after suave update (this exposes storage to everyone)
    address public immutable vault;
    Suave.DataId internal pkDataId;
    address public auctionMaster;
    Auction[] public auctions;
    bool public isInitialized;
    EthJsonRPC settlementRpc;

    constructor(address _vault, string memory _settlementChainRpc) {
        settlementRpc = new EthJsonRPC(_settlementChainRpc);
        vault = _vault;
    }

    function confidentialConstructorCallback(Suave.DataId _pkDataId, address pkAddress) public {
        crequire(!isInitialized, "Already initialized");
        pkDataId = _pkDataId;
        auctionMaster = pkAddress;
        isInitialized = true;
    }

    function createAuctionCallback(
        NewAuctionArgs memory auctionArgs, 
        bytes32 tokenHash,
        address auctioneer
    ) external {
        uint64 until = uint64(block.timestamp) + auctionArgs.auctionDuration;
        Auction storage newAuction = auctions.push();
        newAuction.status = AuctionStatus.LIVE;
        newAuction.payoutAddress = auctionArgs.payoutAddress;
        newAuction.hashedToken = tokenHash;
        newAuction.until = until;
        newAuction.payoutCollectionDuration = auctionArgs.payoutCollectionDuration;

        emit AuctionCreated(
            auctions.length-1,
            auctioneer,
            tokenHash,
            auctionArgs.payoutAddress,
            until,
            auctionArgs.payoutCollectionDuration
        );
    }

    function cancelAuctionCallback(uint auctionId) external {
        auctions[auctionId].status = AuctionStatus.CANCELLED;
        emit AuctionCancelled(auctionId);
    }

    function submitBidCallback(uint auctionId, BidId bidId, address bidder) external {
        auctions[auctionId].bids++;
        emit BidPlaced(auctionId, bidId, bidder);
    }

    function settleAuctionCallback(
        uint auctionId, 
        BidId winningBidId,
        AuctionPayout memory payout, 
        bytes memory payoutSig
    ) external {
        Auction storage auction = auctions[auctionId];
        auction.status = AuctionStatus.SETTLED;
        emit AuctionSettled(auctionId, winningBidId, payout, payoutSig);
    }

    // ðŸ¤ MEVM Methods

    function confidentialConstructor() external returns (bytes memory) {
        crequire(!isInitialized, "Already initialized");
        string memory pk = Suave.privateKeyGen(Suave.CryptoSignature.SECP256);
        address pkAddress = getAddressForPk(pk);
		Suave.DataId _pkDataId = storePK(bytes(pk));

        return abi.encodeWithSelector(
            this.confidentialConstructorCallback.selector, 
            _pkDataId,
            pkAddress
        );
    }

    function createAuction(NewAuctionArgs memory auctionArgs) external onlyConfidential returns (bytes memory) {
        string memory token = string(Suave.confidentialInputs());
        bytes32 tokenHash = keccak256(abi.encode(token));
        address auctioneer = msg.sender;

        return abi.encodeWithSelector(
            this.createAuctionCallback.selector, 
            auctionArgs,
            tokenHash,
            auctioneer
        );
    }

    function cancelAuction(uint256 auctionId) external onlyConfidential returns (bytes memory) {
        Auction storage auction = auctions[auctionId];
        require(auction.auctioneer == msg.sender, "Only auction master can cancel");
        require(auction.status == AuctionStatus.LIVE, "Auction is not live");
        return abi.encodeWithSelector(this.cancelAuctionCallback.selector, auctionId);
    }

    function submitBid(uint auctionId) external onlyConfidential returns (bytes memory) {
        uint bidAmount = abi.decode(Suave.confidentialInputs(), (uint));
        address bidder = msg.sender;

        checkBidValidity(auctionId, bidder, bidAmount);
        BidId bidId = BidUtils.getBidId(uint128(auctionId), auctions[auctionId].bids);
        Bid memory bid = Bid(bidId, bidder, bidAmount);
        storeBid(bid, auctionId);

        return abi.encodeWithSelector(
            this.submitBidCallback.selector,
            auctionId,
            bid.id, 
            bidder
        );
    }

    function settleAuction(uint auctionId) external onlyConfidential returns (bytes memory) {
        Auction storage auction = auctions[auctionId];
        require(auction.status == AuctionStatus.LIVE, "Auction is not live");
        require(block.timestamp >= auction.until, "Auction has not ended");
        require(auction.bids > 0, "No bids");

        (Bid memory winningBid, uint scndBidAmount) = settleVickeryAuction(auctionId);
        AuctionPayout memory payout = AuctionPayout(auction.payoutAddress, scndBidAmount); // todo: can be return from _vickreyWinnerAndBid
        bytes memory payoutSig = signPayout(payout);

        return abi.encodeWithSelector(
            this.settleAuctionCallback.selector,
            auctionId,
            winningBid.id,
            payout, 
            payoutSig
        );
    }

    function checkBidValidity(uint auctionId, address bidder, uint bidAmount) internal {
        Auction storage auction = auctions[auctionId];
        require(auction.status == AuctionStatus.LIVE, "Auction is not live");
        require(block.timestamp < auction.until, "Auction has ended");
        require(bidAmount > 0, "Bid amount should be greater than zero");

        bool bidderHasFunds = userHasSufficientFunds(
            bidder, 
            bidAmount, 
            auction.until + auction.payoutCollectionDuration
        );
        require(bidderHasFunds, "Insufficient funds");
    }

    function signPayout(AuctionPayout memory payout) internal returns (bytes memory sig) {
        string memory pk = retreivePK();
        bytes32 digest = keccak256(abi.encode(payout));
        sig = Suave.signMessage(abi.encodePacked(digest), Suave.CryptoSignature.SECP256, pk);
    }

    function storeBid(Bid memory bid, uint auctionId) internal {
        string memory namespace = string(abi.encodePacked(auctionId));
        address[] memory peekers = new address[](3);
        peekers[0] = address(this);
		peekers[1] = Suave.FETCH_DATA_RECORDS;
		peekers[2] = Suave.CONFIDENTIAL_RETRIEVE;
		Suave.DataRecord memory secretBid = Suave.newDataRecord(0, genericPeekers, genericPeekers, namespace);
		Suave.confidentialStore(secretBid.id, namespace, abi.encode(bid));
    }

    function fetchBids(uint auctionId) internal returns (Bid[] memory){
        string memory namespace = string(abi.encodePacked(BID_NAMESPACE,auctionId));
        Suave.DataRecord[] memory dataRecords = Suave.fetchDataRecords(0, namespace);
        Bid[] memory bids = new Bid[](dataRecords.length);
        for (uint i = 0; i < dataRecords.length; i++) {
            bytes memory bidBytes = Suave.confidentialRetrieve(dataRecords[i].id, namespace);
            Bid memory bid = abi.decode(bidBytes, (Bid));
            bids[i] = bid;
        }
        return bids;
    }

    function storePK(bytes memory pk) internal returns (Suave.DataId) {
		address[] memory peekers = new address[](3);
		peekers[0] = address(this);
		peekers[1] = Suave.FETCH_DATA_RECORDS;
		peekers[2] = Suave.CONFIDENTIAL_RETRIEVE;
		Suave.DataRecord memory secretBid = Suave.newDataRecord(0, genericPeekers, genericPeekers, PK_NAMESPACE);
		Suave.confidentialStore(secretBid.id, PK_NAMESPACE, pk);
		return secretBid.id;
	}

    function retreivePK() internal returns (string memory) {
        bytes memory pkBytes =  Suave.confidentialRetrieve(pkDataId, PK_NAMESPACE);
        return string(pkBytes);
    }

    function userHasSufficientFunds(
        address user, 
        uint amount, 
        uint auctionEndPlusClaimTime
    ) public returns (bool) {
        bytes memory callRes = settlementRpc.call(vault, abi.encodeWithSignature("getBalance(address)", user));
        (uint balance, uint64 lockedUntil) = abi.decode(callRes, (uint256, uint64));
        return balance >= amount && lockedUntil >= auctionEndPlusClaimTime;
    }

    function arbitaryCall(address to, bytes memory data) external onlyConfidential returns (bytes memory) {
        return settlementRpc.call(to, data);
    }

    function userHasSufficientFundsRaw(
        address user, 
        uint amount, 
        uint auctionEndPlusClaimTime
    ) public returns (bytes memory) {
        return settlementRpc.call(vault, abi.encodeWithSignature("getBalance(address)", user));
    }

    function userHasSufficientFundsRaw2(
        address _vault,
        address user, 
        uint amount, 
        uint auctionEndPlusClaimTime
    ) public returns (bytes memory) {
        return settlementRpc.call(_vault, abi.encodeWithSignature("getBalance(address)", user));
    }

    // todo: what if multiple bidders bid the same? - store time for FIFO
    // Assumes auction status is checked on a higher lvl
    function settleVickeryAuction(
        uint auctionId
    ) internal returns (Bid memory winningBid, uint scndBestBidAmount) 
    {
        Bid[] memory bids = fetchBids(auctionId);
        for (uint i = 0; i < bids.length; ++i) {
            Bid memory bid = bids[i];
            if (bid.amount > winningBid.amount) {
                scndBestBidAmount = winningBid.amount;
                winningBid = bid;
            } else if (bid.amount > scndBestBidAmount) {
                scndBestBidAmount = bid.amount;
            }
        }
        if (scndBestBidAmount == 0) {
            scndBestBidAmount = winningBid.amount;
        }
    }

    fallback() external {}

}

contracts/lib/AuctionUtils.sol.content:
pragma solidity ^0.8.8;


type BidId is uint256;

enum AuctionStatus {
    LIVE,
    CANCELLED,
    SETTLED
}

struct Auction {
    AuctionStatus status;
    uint128 bids;
    address payoutAddress;
    bytes32 hashedToken;
    uint64 until;
    uint64 payoutCollectionDuration;
    address auctioneer;
}
struct Bid {
    BidId id;
    address bidder;
    uint256 amount;
}
struct NewAuctionArgs {
    uint64 auctionDuration;
    uint64 payoutCollectionDuration;
    address payoutAddress;
}
struct AuctionPayout {
    address account;
    uint amount;
}

library BidUtils {

    function getBidId(uint128 auctionId, uint128 bidIndex) internal pure returns (BidId) {
        return BidId.wrap(uint(auctionId << 128 | bidIndex));
    }

    function unpackBidId(BidId bidId) internal pure returns (uint128 auctionId, uint128 bidIndex) {
        auctionId = uint128(BidId.unwrap(bidId)) >> 128;
        bidIndex = uint128(BidId.unwrap(bidId));
    }

}

contracts/lib/SigUtils.sol.content:
pragma solidity ^0.8.8;

import { Suave } from "lib/suave-std/src/suavelib/Suave.sol";


function getAddressForPk(string memory pk) returns (address) {
    bytes32 digest = keccak256(abi.encode("yo"));
    bytes memory sig = Suave.signMessage(abi.encodePacked(digest), Suave.CryptoSignature.SECP256, pk);
    return recoverSigner(digest, sig);
}

function recoverSigner(bytes32 _ethSignedMessageHash, bytes memory _signature) pure returns (address) {
    (bytes32 r, bytes32 s, uint8 v) = splitSignature(_signature);
    return ecrecover(_ethSignedMessageHash, v, r, s);
}

function splitSignature(bytes memory sig) pure returns (bytes32 r, bytes32 s, uint8 v) {
    require(sig.length == 65, "invalid signature length");
    assembly {
        r := mload(add(sig, 32))
        s := mload(add(sig, 64))
        v := byte(0, mload(add(sig, 96)))
    }
    if (v < 27) {
        v += 27;
    }
}

contracts/lib/SuaveContract.sol.content:
pragma solidity ^0.8.9;

import { Suave } from "lib/suave-std/src/suavelib/Suave.sol";


abstract contract SuaveContract {
	error SuaveError(string message);
	error SuaveErrorWithData(string message, bytes data);

	modifier onlyConfidential() {
		crequire(Suave.isConfidential(), "Not confidential");
		_;
	}

	function crequire(bool condition, string memory message) internal pure {
		if (!condition) {
			revert SuaveError(message);
		}
	}
}

